    public void createdImg(byte[] data) {
        try {
            String name = LocalDateTime.now().toString() + "_output.jpg";
            ByteArrayInputStream bis = new ByteArrayInputStream(data);
            BufferedImage bImage = ImageIO.read(bis);
            ImageIO.write(bImage, "jpg", new File("src/main/java/com/example/demo/imagemCreada/" + name) );
            System.out.println("image created");
        } catch (Exception e) {   
            e.printStackTrace();
        }
    }
